configText:
params {
   reads_bam = '/.nextflow/assets/seqeralabs/nf-hello-genomics/data/sample_bams.txt'
   reference = '/.nextflow/assets/seqeralabs/nf-hello-genomics/data/ref/ref.fasta'
   reference_index = '/.nextflow/assets/seqeralabs/nf-hello-genomics/data/ref/ref.fasta.fai'
   reference_dict = '/.nextflow/assets/seqeralabs/nf-hello-genomics/data/ref/ref.dict'
   intervals = '/.nextflow/assets/seqeralabs/nf-hello-genomics/data/ref/intervals.bed'
   cohort_name = 'family_trio'
   outdir = 'results'
}

docker {
   fixOwnership = true
}

process {
   cpus = 2
   memory = '2 GB'
   withName:GATK_JOINTGENOTYPING {
      cpus = 8
   }
   executor = 'awsbatch'
   queue = 'TowerForge-34gPOnmW8FGFz9Hh1YSXHw-work'
   resourceLabels = [workspace:'showcase', owner:'seqera']
   scratch = false
}

timeline {
   enabled = true
   file = 'timeline-2VeeQznQdp3rxo.html'
}

aws {
   region = 'eu-west-1'
   client {
      uploadChunkSize = 10485760
   }
   batch {
      maxSpotAttempts = 0
      volumes = '/scratch/fusion:/tmp'
      executionRole = 'arn:aws:iam::893329243595:role/TowerForge-34gPOnmW8FGFz9Hh1YSXHw-ExecutionRole'
   }
}

workDir = 's3://nf-tower-bucket/scratch/2VeeQznQdp3rxo'

wave {
   enabled = true
   endpoint = 'https://wave.seqera.io'
}

fusion {
   enabled = true
}

runName = 'fabulous_banach'

tower {
   enabled = true
   endpoint = 'https://api.cloud.seqera.io'
}

cloudcache {
   enabled = true
   path = 's3://nf-tower-bucket/.cache'
}
